    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        window?.setBackgroundDrawableResource(android.R.drawable.screen_background_dark_transparent)
        //设置弹窗在底部
        val window = this.window
        window!!.setGravity(Gravity.BOTTOM)
        window!!.setLayout(WindowManager.LayoutParams.MATCH_PARENT, WindowManager.LayoutParams.MATCH_PARENT)
        init()
        setEvent()
    }

    init {
        activity = acy
        bgUrl = bg_url
        qrcodeUrl = qrcode_url
        infoName = info
        productName = product
        dateStr = date
        contentStr = content

        if (!TextUtils.isEmpty(share_track)) {
            try {
                val trackData = URLDecoder.decode(share_track, "utf-8")
                shareTrack = JSON.parse(trackData) as MutableMap<String?, Any?>
                if (shareTrack?.containsKey("track_name") == true){

                    //track_name 神策埋点事件名
                    eventName = shareTrack?.get("track_name") as String?
                    shareTrack?.remove("track_name")
                    Logger.d(
                            TAG,
                            "share_track = $share_track"
                    )
                    Logger.d(TAG, "eventName = $eventName")
                    for (map in shareTrack?.entries!!) {
                        Logger.d(
                                TAG,
                                (map as Map.Entry<*, *>).key.toString() + " " + (map as Map.Entry<*, *>).value
                        )
                    }
                }
            } catch (e: Exception) {
                e.printStackTrace()
            }
        }
    }

    override fun dismiss() {
        try {
            wxShareAction = null
            ivPoster!!.setImageDrawable(null)
            poster!!.recycle()
            poster = null
        } catch (e: Exception) {
            //do nothing
        }
        super.dismiss()
    }

    private fun init() {
        initView()
        initListener()
        wxShareAction = WXShareAction(context)

        //根据url生成海报；生成之前只展示进度条
        loadData(activity!!)
    }

