    private fun initView() {
        setContentView(getContentLayoutId())
        rlProgress = findViewById<View>(R.id.rl_progress) as RelativeLayout
        llContent = findViewById<View>(R.id.ll_content) as LinearLayout
        ivPoster = findViewById<View>(R.id.iv_info_poster) as ImageView
        llWechat = findViewById<View>(R.id.ll_wechat) as LinearLayout
        llWxfriend = findViewById<View>(R.id.ll_wxfriend) as LinearLayout
        llWwk = findViewById<View>(R.id.ll_wwk) as LinearLayout
        llSave = findViewById<View>(R.id.ll_info_poster_save) as LinearLayout
