    open fun getContentLayoutId():Int{
        return R.layout.dialog_info_disclousure_poster;
    }
    //留给子类自定义实现
    open fun setEvent(){

    }
    private fun initListener() {
        llWechat!!.setOnClickListener(this)
        llWxfriend!!.setOnClickListener(this)
        llWwk!!.setOnClickListener(this)
        llSave!!.setOnClickListener(this)
        rlCancel!!.setOnClickListener(this)
    }

    private fun savePoster() {
        if (poster != null && AlbumHelper.addSignatureToGallery(context, poster)) {
            ToastUtils.show("成功保存到相册")
        } else {
            ToastUtils.show( "保存相册失败")
        }
    }

    override fun onClick(v: View) {
        when (v.id) {
            R.id.ll_wechat -> {
                wxShareAction!!.sharedImageByBitmap(
                        Bitmap.createBitmap(
                                poster!!
                        ), false
                )

                sensorsTrack("微信")
                dismiss()
            }

            R.id.ll_wxfriend -> {
                wxShareAction!!.sharedImageByBitmap(
                    Bitmap.createBitmap(
                        poster!!
                    ), true
                )

                sensorsTrack("微信朋友圈")
                dismiss()
            }

            R.id.ll_wwk -> {
                WeComShareManager.instance.weComShareImg(
                        Bitmap.createBitmap(
                                poster!!
                        )
                )
                sensorsTrack("企业微信")
                dismiss()
            }
            R.id.ll_info_poster_save -> {
                savePoster()
                sensorsTrack("下载图片")
                dismiss()
            }
            R.id.rl_cancel -> {
                dismiss()
            }
        }
    }


    //加载背景bigtmap和二维码的bigtmap，并合成
    open fun loadData(activity: Activity){
        //根据背景url加载背景图
        if (StringUtils.isNotEmptyJava(bgUrl) && bgUrl.startsWith("http")){

            Glide.with(ApplicationUtil.getContext()).asBitmap()
                .load(bgUrl).diskCacheStrategy(DiskCacheStrategy.RESOURCE)
                .into(object : SimpleTarget<Bitmap?>() {
                    override fun onResourceReady(
                        bgBitmap: Bitmap,
                        transition: Transition<in Bitmap?>?
                    ) {
                        loadQrBitmap(bgBitmap)
                    }
                })
        }else if(StringUtils.isNotEmptyJava(bgUrl)){//认为bgUrl是base64
            val bgBitmap = BitmapUtil.getBitmapFromBase64(bgUrl)
            loadQrBitmap(bgBitmap)
        }else{
            //使用默认背景图
            val bgBitmap = BitmapFactory.decodeResource(activity.resources, R.drawable.ic_launcher_background)
            loadQrBitmap(bgBitmap)
        }
    }

    //加载二维码
    open fun loadQrBitmap(bgBitmap: Bitmap?){
        //根据url来获取二维码图片
        if (StringUtils.isNotEmpty(qrcodeUrl) && qrcodeUrl.startsWith("http")) {

            Glide.with(ApplicationUtil.getContext()).asBitmap()
                .load(qrcodeUrl).diskCacheStrategy(DiskCacheStrategy.RESOURCE)
                .into(object : SimpleTarget<Bitmap?>() {
                    override fun onResourceReady(
                        qrcodeBitmap: Bitmap,
                        transition: Transition<in Bitmap?>?
                    ) {
                        //展示海报和分享按钮
                        poster = generatePoster(bgBitmap, qrcodeBitmap)

                        if (poster != null) {
                            ivPoster!!.setImageDrawable(BitmapDrawable(context.resources, poster))
                        }
                        rlProgress?.visibility = View.GONE
                        llContent?.visibility = View.VISIBLE
                    }
                })

        } else if (StringUtils.isNotEmpty(qrcodeUrl)) {//认为bgUrl是base64
            var qrBitmap = BitmapUtil.getBitmapFromBase64(qrcodeUrl)
            //展示海报和分享按钮
            poster = generatePoster(bgBitmap, qrBitmap)
            if (poster != null) {
                ivPoster!!.setImageDrawable(BitmapDrawable(context.resources, poster))
            }
            rlProgress?.visibility = View.GONE
            llContent?.visibility = View.VISIBLE

        }else{//没有二维码
            poster = generatePoster(bgBitmap, null)
            if (poster != null) {
                ivPoster!!.setImageDrawable(BitmapDrawable(context.resources, poster))
            }
            rlProgress?.visibility = View.GONE
            llContent?.visibility = View.VISIBLE
        }
    }

    abstract fun generatePoster(bg: Bitmap?, qr: Bitmap?): Bitmap?
    fun resizeImage(bitmap: Bitmap): Bitmap {
        val width = bitmap.width
        val height = bitmap.height
        if (width == IMG_WIDTH) {
            return bitmap
        }
        val newWidth = IMG_WIDTH
        val newHeight = IMG_HEIGHT
        val scaleWidth = newWidth.toFloat() / width
        val scaleHeight = newHeight.toFloat()/height
        //以小的为准，保证ImageView能显示全部的背景图
        val scale=if(scaleWidth<scaleHeight)  scaleWidth else scaleHeight
        val matrix = Matrix()
        matrix.postScale(scale, scale)
        return Bitmap.createBitmap(
                bitmap, 0, 0, width,
                height, matrix, true
        )
    }
}